import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [roles, setRoles] = useState([]);
  const [selectedRole, setSelectedRole] = useState('');
  const [resumeFile, setResumeFile] = useState(null);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch available roles on component mount
  useEffect(() => {
    fetchRoles();
  }, []);

  const fetchRoles = async () => {
    try {
      const response = await axios.get('/api/roles');
      setRoles(response.data.roles);
      if (response.data.roles.length > 0) {
        setSelectedRole(response.data.roles[0]);
      }
    } catch (err) {
      setError('Failed to fetch roles');
    }
  };

  const handleFileChange = (e) => {
    setResumeFile(e.target.files[0]);
  };

  const handleRoleChange = (e) => {
    setSelectedRole(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!resumeFile) {
      setError('Please upload a resume');
      return;
    }
    if (!selectedRole) {
      setError('Please select a role');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('role', selectedRole);

    try {
      const response = await axios.post('/api/analyze', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setAnalysisResults(response.data);
    } catch (err) {
      setError('Failed to analyze skills: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const exportResults = async () => {
    if (!analysisResults) return;

    try {
      const exportData = {
        ...analysisResults,
        role: selectedRole
      };
      
      const response = await axios.post('/api/export', exportData, {
        responseType: 'blob'
      });
      
      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', response.headers['content-disposition']?.split('filename=')[1] || 'skill_gap_analysis.csv');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      setError('Failed to export results');
    }
  };

  // Chart data preparation
  const getSkillMatchChartData = () => {
    if (!analysisResults) return null;
    
    return {
      labels: ['Matched', 'Partial Match', 'Missing'],
      datasets: [
        {
          label: 'Skills',
          data: [
            analysisResults.matched_skills.length,
            analysisResults.partial_skills.length,
            analysisResults.missing_skills.length
          ],
          backgroundColor: [
            '#10B981', // Green for matched
            '#F59E0B', // Amber for partial
            '#EF4444'  // Red for missing
          ],
          borderColor: [
            '#047857',
            '#B45309',
            '#B91C1C'
          ],
          borderWidth: 1
        }
      ]
    };
  };

  const getOverallMatchData = () => {
    if (!analysisResults) return null;
    
    return {
      labels: ['Match'],
      datasets: [
        {
          label: 'Overall Match Percentage',
          data: [analysisResults.overall_match],
          backgroundColor: '#4F46E5',
          borderColor: '#4338CA',
          borderWidth: 1
        }
      ]
    };
  };

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div className="header-content">
          <h1>
            AI Role-Based Skill Gap Analyzer
          </h1>
          <p>
            Upload your resume and select your target role to identify skill gaps and get upskilling recommendations.
          </p>
        </div>
      </header>

      <main className="main-content">
        {/* Error message */}
        {error && (
          <div className="alert alert-error">
            {error}
          </div>
        )}

        {/* Analysis Form */}
        <div className="card">
          <h2 className="card-title">Analyze Your Skills</h2>
          <form onSubmit={handleSubmit}>
            <div>
              {/* Role Selection */}
              <div className="form-group">
                <label htmlFor="role" className="form-label">
                  Select Target Role
                </label>
                <select
                  id="role"
                  value={selectedRole}
                  onChange={handleRoleChange}
                  className="form-select"
                >
                  {roles.map((role) => (
                    <option key={role} value={role}>
                      {role}
                    </option>
                  ))}
                </select>
              </div>

              {/* Resume Upload */}
              <div className="form-group">
                <label htmlFor="resume" className="form-label">
                  Upload Resume (PDF, DOCX, TXT)
                </label>
                <input
                  type="file"
                  id="resume"
                  accept=".pdf,.docx,.txt"
                  onChange={handleFileChange}
                  className="form-input"
                />
              </div>

              {/* Submit Button */}
              <button
                type="submit"
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? 'Analyzing...' : 'Analyze Skills'}
              </button>
            </div>
          </form>
        </div>

        {/* Loading Indicator */}
        {loading && (
          <div className="loading">
            <div className="spinner"></div>
            <p>Analyzing your resume and comparing with {selectedRole} requirements...</p>
          </div>
        )}

        {/* Results */}
        {analysisResults && !loading && (
          <div className="results-section">
            <div className="card">
              <div className="results-header">
                <h2 className="card-title">Analysis Results for {selectedRole}</h2>
                <button
                  onClick={exportResults}
                  className="btn btn-secondary"
                >
                  Export Results
                </button>
              </div>

              {/* Overall Match */}
              <div className="result-card">
                <h3>Overall Match</h3>
                <div className="progress-bar">
                  <div 
                    className={`progress-fill progress-high`} 
                    style={{ width: `${analysisResults.overall_match}%` }}
                  ></div>
                </div>
                <p>{analysisResults.overall_match.toFixed(1)}% match with {selectedRole} requirements</p>
              </div>

              {/* Charts */}
              <div className="chart-container">
                <h3>Skill Match Distribution</h3>
                <Bar data={getSkillMatchChartData()} />
              </div>

              <div className="chart-container">
                <h3>Overall Match Percentage</h3>
                <Doughnut data={getOverallMatchData()} />
              </div>

              {/* Detailed Results */}
              <div className="results-grid">
                {/* Matched Skills */}
                <div className="result-card">
                  <h3>
                    <span className="match-indicator match-high"></span>
                    Matched Skills ({analysisResults.matched_skills.length})
                  </h3>
                  <ul className="skill-list">
                    {analysisResults.matched_skills.map((skill, index) => (
                      <li key={index} className="skill-item">
                        {skill}
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Partial Match Skills */}
                <div className="result-card">
                  <h3>
                    <span className="match-indicator match-medium"></span>
                    Partial Match Skills ({analysisResults.partial_skills.length})
                  </h3>
                  <ul className="skill-list">
                    {analysisResults.partial_skills.map((skill, index) => (
                      <li key={index} className="skill-item">
                        {skill}
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Missing Skills */}
                <div className="result-card">
                  <h3>
                    <span className="match-indicator match-low"></span>
                    Missing Skills ({analysisResults.missing_skills.length})
                  </h3>
                  <ul className="skill-list">
                    {analysisResults.missing_skills.map((skill, index) => (
                      <li key={index} className="skill-item">
                        {skill}
                      </li>
                    ))}
                  </ul>
                </div>
              </div>

              {/* Recommendations */}
              <div className="result-card">
                <h3>Upskilling Recommendations</h3>
                <ul className="skill-list">
                  {analysisResults.recommendations.map((rec, index) => (
                    <li key={index} className="skill-item">
                      {rec}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
