import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [roles, setRoles] = useState([]);
  const [selectedRole, setSelectedRole] = useState('');
  const [resumeFile, setResumeFile] = useState(null);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [cssWorking, setCssWorking] = useState(false);

  // Fetch available roles on component mount
  useEffect(() => {
    fetchRoles();
    
    // Test if Tailwind CSS is working
    const testElement = document.createElement('div');
    testElement.className = 'bg-blue-500 text-white p-4 rounded';
    testElement.textContent = 'Test Element';
    testElement.style.display = 'none'; // Hide it
    
    // Add to body temporarily
    document.body.appendChild(testElement);
    
    // Get computed styles
    const styles = window.getComputedStyle(testElement);
    const bgColor = styles.backgroundColor;
    
    // Remove test element
    document.body.removeChild(testElement);
    
    // Check if Tailwind classes are applied (blue-500 should be #3b82f6)
    setCssWorking(bgColor === 'rgb(59, 130, 246)');
  }, []);

  const fetchRoles = async () => {
    try {
      const response = await axios.get('/api/roles');
      setRoles(response.data.roles);
      if (response.data.roles.length > 0) {
        setSelectedRole(response.data.roles[0]);
      }
    } catch (err) {
      setError('Failed to fetch roles');
    }
  };

  const handleFileChange = (e) => {
    setResumeFile(e.target.files[0]);
  };

  const handleRoleChange = (e) => {
    setSelectedRole(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!resumeFile) {
      setError('Please upload a resume');
      return;
    }
    if (!selectedRole) {
      setError('Please select a role');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('role', selectedRole);

    try {
      const response = await axios.post('/api/analyze', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setAnalysisResults(response.data);
    } catch (err) {
      setError('Failed to analyze skills: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const exportResults = async () => {
    if (!analysisResults) return;

    try {
      const exportData = {
        ...analysisResults,
        role: selectedRole
      };
      
      const response = await axios.post('/api/export', exportData, {
        responseType: 'blob'
      });
      
      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', response.headers['content-disposition']?.split('filename=')[1] || 'skill_gap_analysis.csv');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      setError('Failed to export results');
    }
  };

  // Chart data preparation
  const getSkillMatchChartData = () => {
    if (!analysisResults) return null;
    
    return {
      labels: ['Matched', 'Partial Match', 'Missing'],
      datasets: [
        {
          label: 'Skills',
          data: [
            analysisResults.matched_skills.length,
            analysisResults.partial_skills.length,
            analysisResults.missing_skills.length
          ],
          backgroundColor: [
            '#10B981', // Green for matched
            '#F59E0B', // Amber for partial
            '#EF4444'  // Red for missing
          ],
          borderColor: [
            '#047857',
            '#B45309',
            '#B91C1C'
          ],
          borderWidth: 1
        }
      ]
    };
  };

  const getOverallMatchData = () => {
    if (!analysisResults) return null;
    
    return {
      labels: ['Match'],
      datasets: [
        {
          label: 'Overall Match Percentage',
          data: [analysisResults.overall_match],
          backgroundColor: '#4F46E5',
          borderColor: '#4338CA',
          borderWidth: 1
        }
      ]
    };
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">
            AI Role-Based Skill Gap Analyzer
          </h1>
          <p className="mt-2 text-gray-600">
            Upload your resume and select your target role to identify skill gaps and get upskilling recommendations.
          </p>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {/* Error message */}
        {error && (
          <div className="mb-6 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded relative">
            {error}
          </div>
        )}

        {/* Analysis Form */}
        <div className="bg-white shadow overflow-hidden sm:rounded-lg mb-8">
          <div className="px-4 py-5 sm:p-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Analyze Your Skills</h2>
            <form onSubmit={handleSubmit}>
              <div className="grid grid-cols-1 gap-y-6 gap-x-4 sm:grid-cols-2">
                {/* Role Selection */}
                <div>
                  <label htmlFor="role" className="block text-sm font-medium text-gray-700">
                    Select Target Role
                  </label>
                  <select
                    id="role"
                    value={selectedRole}
                    onChange={handleRoleChange}
                    className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                  >
                    {roles.map((role) => (
                      <option key={role} value={role}>
                        {role}
                      </option>
                    ))}
                  </select>
                </div>

                {/* File Upload */}
                <div>
                  <label htmlFor="resume" className="block text-sm font-medium text-gray-700">
                    Upload Resume
                  </label>
                  <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                    <div className="space-y-1 text-center">
                      <div className="flex text-sm text-gray-600">
                        <label
                          htmlFor="resume"
                          className="relative cursor-pointer bg-white rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-indigo-500"
                        >
                          <span>Upload a file</span>
                          <input
                            id="resume"
                            name="resume"
                            type="file"
                            className="sr-only"
                            onChange={handleFileChange}
                            accept=".pdf,.docx,.txt"
                          />
                        </label>
                        <p className="pl-1">or drag and drop</p>
                      </div>
                      <p className="text-xs text-gray-500">
                        PDF, DOCX, or TXT up to 10MB
                      </p>
                      {resumeFile && (
                        <p className="text-xs text-gray-500 mt-2">
                          Selected: {resumeFile.name}
                        </p>
                      )}
                    </div>
                  </div>
                </div>
              </div>

              <div className="mt-6">
                <button
                  type="submit"
                  disabled={loading}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
                >
                  {loading ? (
                    <>
                      <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Analyzing...
                    </>
                  ) : (
                    'Analyze Skills'
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* Results */}
        {analysisResults && (
          <div className="space-y-8">
            {/* Overall Match */}
            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h2 className="text-xl font-semibold text-gray-800 mb-4">Overall Match</h2>
                <div className="flex items-center">
                  <div className="w-32 h-32">
                    <Doughnut 
                      data={getOverallMatchData()} 
                      options={{
                        responsive: true,
                        plugins: {
                          legend: {
                            display: false
                          },
                          tooltip: {
                            callbacks: {
                              label: function(context) {
                                return `${context.label}: ${context.raw}%`;
                              }
                            }
                          }
                        }
                      }} 
                    />
                  </div>
                  <div className="ml-6">
                    <p className="text-4xl font-bold text-indigo-600">
                      {analysisResults.overall_match}%
                    </p>
                    <p className="text-gray-600 mt-2">Overall Match Percentage</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Skill Match Overview */}
            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h2 className="text-xl font-semibold text-gray-800 mb-4">Skill Match Overview</h2>
                <div className="h-64">
                  <Bar 
                    data={getSkillMatchChartData()} 
                    options={{
                      responsive: true,
                      maintainAspectRatio: false,
                      plugins: {
                        legend: {
                          position: 'top',
                        },
                        title: {
                          display: true,
                          text: 'Skill Match Distribution'
                        }
                      }
                    }} 
                  />
                </div>
              </div>
            </div>

            {/* Extracted Skills */}
            <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
              {/* Resume Skills */}
              <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                <div className="px-4 py-5 sm:p-6">
                  <h2 className="text-xl font-semibold text-gray-800 mb-4">Your Resume Skills</h2>
                  <div>
                    <h3 className="text-lg font-medium text-gray-700 mb-2">Technical Skills</h3>
                    <div className="flex flex-wrap">
                      {analysisResults.resume_tech_skills.map((skill, index) => (
                        <span 
                          key={index} 
                          className="inline-block bg-indigo-100 text-indigo-800 text-sm font-medium mr-2 mb-2 px-3 py-1 rounded-full"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                  <div className="mt-4">
                    <h3 className="text-lg font-medium text-gray-700 mb-2">Soft Skills</h3>
                    <div className="flex flex-wrap">
                      {analysisResults.resume_soft_skills.map((skill, index) => (
                        <span 
                          key={index} 
                          className="inline-block bg-green-100 text-green-800 text-sm font-medium mr-2 mb-2 px-3 py-1 rounded-full"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>

              {/* Role Skills */}
              <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                <div className="px-4 py-5 sm:p-6">
                  <h2 className="text-xl font-semibold text-gray-800 mb-4">Required Skills for {selectedRole}</h2>
                  <div>
                    <h3 className="text-lg font-medium text-gray-700 mb-2">Technical Skills</h3>
                    <div className="flex flex-wrap">
                      {analysisResults.jd_tech_skills.map((skill, index) => (
                        <span 
                          key={index} 
                          className="inline-block bg-indigo-100 text-indigo-800 text-sm font-medium mr-2 mb-2 px-3 py-1 rounded-full"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                  <div className="mt-4">
                    <h3 className="text-lg font-medium text-gray-700 mb-2">Soft Skills</h3>
                    <div className="flex flex-wrap">
                      {analysisResults.jd_soft_skills.map((skill, index) => (
                        <span 
                          key={index} 
                          className="inline-block bg-green-100 text-green-800 text-sm font-medium mr-2 mb-2 px-3 py-1 rounded-full"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Missing Skills */}
            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h2 className="text-xl font-semibold text-gray-800 mb-4">Missing Skills</h2>
                <div className="flex flex-wrap">
                  {analysisResults.missing_skills.length > 0 ? (
                    analysisResults.missing_skills.map((skill, index) => (
                      <span 
                        key={index} 
                        className="inline-block bg-red-100 text-red-800 text-sm font-medium mr-2 mb-2 px-3 py-1 rounded-full"
                      >
                        {skill}
                      </span>
                    ))
                  ) : (
                    <p className="text-green-600 font-medium">No missing skills! Great match!</p>
                  )}
                </div>
              </div>
            </div>

            {/* Recommendations */}
            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h2 className="text-xl font-semibold text-gray-800 mb-4">Upskilling Recommendations</h2>
                <div className="space-y-4">
                  {analysisResults.recommendations.map((rec, index) => (
                    <div key={index} className="flex">
                      <div className="flex-shrink-0">
                        <span className="inline-flex items-center justify-center h-6 w-6 rounded-full bg-indigo-100 text-indigo-800">
                          {index + 1}
                        </span>
                      </div>
                      <div className="ml-3">
                        <p className="text-gray-700">{rec}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Export Options */}
            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h2 className="text-xl font-semibold text-gray-800 mb-4">Export Results</h2>
                <button
                  onClick={exportResults}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  Export as CSV
                </button>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
