import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [roles, setRoles] = useState([]);
  const [selectedRole, setSelectedRole] = useState('');
  const [resumeFile, setResumeFile] = useState(null);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch available roles on component mount
  useEffect(() => {
    fetchRoles();
  }, []);

  const fetchRoles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/roles');
      setRoles(response.data.roles);
      if (response.data.roles.length > 0) {
        setSelectedRole(response.data.roles[0]);
      }
    } catch (err) {
      setError('Failed to fetch roles');
    }
  };

  const handleFileChange = (e) => {
    setResumeFile(e.target.files[0]);
  };

  const handleRoleChange = (e) => {
    setSelectedRole(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!resumeFile) {
      setError('Please upload a resume');
      return;
    }
    if (!selectedRole) {
      setError('Please select a role');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('role', selectedRole);

    try {
      const response = await axios.post('http://localhost:5000/api/analyze', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setAnalysisResults(response.data);
    } catch (err) {
      setError('Failed to analyze skills: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const exportResults = async () => {
    if (!analysisResults) return;

    try {
      const exportData = {
        ...analysisResults,
        role: selectedRole
      };
      
      const response = await axios.post('http://localhost:5000/api/export', exportData, {
        responseType: 'blob'
      });
      
      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', response.headers['content-disposition']?.split('filename=')[1] || 'skill_gap_analysis.csv');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      setError('Failed to export results');
    }
  };

  // Chart data preparation
  const getSkillMatchChartData = () => {
    if (!analysisResults) return null;
    
    return {
      labels: ['Matched', 'Partial Match', 'Missing'],
      datasets: [
        {
          label: 'Skills',
          data: [
            analysisResults.matched_skills.length,
            analysisResults.partial_skills.length,
            analysisResults.missing_skills.length
          ],
          backgroundColor: [
            '#10B981', // Green for matched
            '#F59E0B', // Amber for partial
            '#EF4444'  // Red for missing
          ],
          borderColor: [
            '#047857',
            '#B45309',
            '#B91C1C'
          ],
          borderWidth: 1
        }
      ]
    };
  };

  const getOverallMatchData = () => {
    if (!analysisResults) return null;
    
    return {
      labels: ['Match'],
      datasets: [
        {
          label: 'Overall Match Percentage',
          data: [analysisResults.overall_match],
          backgroundColor: '#4F46E5',
          borderColor: '#4338CA',
          borderWidth: 1
        }
      ]
    };
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex flex-col">
      {/* Header */}
      <header className="bg-gradient-to-r from-gray-800 to-gray-900 text-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
            <div>
              <h1 className="text-3xl font-bold flex items-center gap-2">
                AI Role-Based Skill Gap Analyzer
                <span className="bg-gradient-to-r from-blue-500 to-indigo-600 text-xs font-semibold px-2 py-1 rounded-full">Beta</span>
              </h1>
              <p className="mt-2 text-gray-300">
                Upload your resume and select your target role to identify skill gaps and get upskilling recommendations.
              </p>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 flex-grow w-full">
        {/* Error message */}
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6 flex items-center gap-3 relative">
            <div className="text-xl">‚ö†Ô∏è</div>
            <div className="flex-grow">{error}</div>
            <button 
              onClick={() => setError('')}
              className="text-red-500 hover:text-red-700 text-2xl leading-none"
            >
              √ó
            </button>
          </div>
        )}

        {/* Analysis Form */}
        <div className="bg-white rounded-xl shadow-lg p-6 mb-8 transition-all duration-300 hover:shadow-xl">
          <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-2">
            <span>üîç</span> Analyze Your Skills
          </h2>
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Role Selection */}
              <div>
                <label htmlFor="role" className="block text-sm font-medium text-gray-700 mb-2">
                  Select Target Role
                </label>
                <select
                  id="role"
                  value={selectedRole}
                  onChange={handleRoleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                  disabled={loading}
                >
                  {roles.map((role) => (
                    <option key={role} value={role}>
                      {role}
                    </option>
                  ))}
                </select>
              </div>

              {/* Resume Upload */}
              <div>
                <label htmlFor="resume" className="block text-sm font-medium text-gray-700 mb-2">
                  Upload Resume (PDF, DOCX, TXT)
                </label>
                <div className="relative">
                  <input
                    type="file"
                    id="resume"
                    accept=".pdf,.docx,.txt"
                    onChange={handleFileChange}
                    className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                    disabled={loading}
                  />
                  <div className="px-4 py-3 border border-gray-300 rounded-lg text-gray-500 bg-gray-50 hover:bg-gray-100 transition-colors cursor-pointer">
                    {resumeFile ? resumeFile.name : 'Choose a file or drag it here'}
                  </div>
                </div>
                <p className="mt-2 text-sm text-gray-500">
                  Supported formats: PDF, DOCX, TXT (Max size: 5MB)
                </p>
              </div>
            </div>

            {/* Submit Button */}
            <div className="mt-8 flex justify-end">
              <button
                type="submit"
                className={`px-6 py-3 rounded-lg font-medium text-white shadow-lg transition-all duration-300 flex items-center gap-2 ${
                  loading 
                    ? 'bg-gray-400 cursor-not-allowed' 
                    : 'bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 hover:shadow-xl transform hover:-translate-y-0.5'
                }`}
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></span>
                    Analyzing...
                  </>
                ) : (
                  'Analyze Skills'
                )}
              </button>
            </div>
          </form>
        </div>

        {/* Loading Indicator */}
        {loading && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-xl shadow-xl p-8 max-w-md w-full mx-4 text-center">
              <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto"></div>
              <h3 className="text-2xl font-bold text-gray-800 mt-4">Analyzing Your Resume</h3>
              <p className="text-gray-600 mt-2">Comparing your skills with {selectedRole} requirements...</p>
              <div className="mt-6 h-2 bg-gray-200 rounded-full overflow-hidden">
                <div className="h-full bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full animate-pulse" style={{ width: '60%' }}></div>
              </div>
            </div>
          </div>
        )}

        {/* Results */}
        {analysisResults && !loading && (
          <div className="mt-8">
            <div className="bg-white rounded-xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl">
              <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
                <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
                  <span>üìä</span> Analysis Results for {selectedRole}
                </h2>
                <div>
                  <button
                    onClick={exportResults}
                    className="px-4 py-2 bg-gradient-to-r from-gray-600 to-gray-700 text-white rounded-lg font-medium hover:from-gray-700 hover:to-gray-800 transition-all duration-300 shadow-md hover:shadow-lg"
                  >
                    Export Results
                  </button>
                </div>
              </div>

              {/* Overall Match */}
              <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border-l-4 border-blue-500 rounded-lg p-6 mb-8">
                <h3 className="text-xl font-semibold text-gray-800 mb-4">Overall Match</h3>
                <div className="text-center">
                  <div className="text-5xl font-bold text-blue-600">{analysisResults.overall_match.toFixed(1)}%</div>
                  <div className="text-gray-600 mt-2">Match with {selectedRole} requirements</div>
                  <div className="mt-4 h-4 bg-gray-200 rounded-full overflow-hidden">
                    <div 
                      className="h-full bg-gradient-to-r from-green-500 to-blue-500 rounded-full transition-all duration-1000 ease-out"
                      style={{ width: `${analysisResults.overall_match}%` }}
                    ></div>
                  </div>
                </div>
              </div>

              {/* Charts */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
                <div className="bg-white border border-gray-200 rounded-lg p-6 shadow-sm">
                  <h3 className="text-xl font-semibold text-gray-800 mb-4">Skill Match Distribution</h3>
                  <Bar data={getSkillMatchChartData()} />
                </div>

                <div className="bg-white border border-gray-200 rounded-lg p-6 shadow-sm">
                  <h3 className="text-xl font-semibold text-gray-800 mb-4">Overall Match Percentage</h3>
                  <Doughnut data={getOverallMatchData()} />
                </div>
              </div>

              {/* Detailed Results */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                {/* Matched Skills */}
                <div className="bg-white border border-gray-200 rounded-lg p-6 shadow-sm">
                  <div className="flex justify-between items-center pb-3 border-b border-gray-200 mb-4">
                    <h3 className="text-lg font-semibold text-gray-800 flex items-center gap-2">
                      <span className="w-3 h-3 bg-green-500 rounded-full"></span>
                      Matched Skills
                    </h3>
                    <span className="bg-green-100 text-green-800 text-sm font-semibold px-2 py-1 rounded-full">
                      {analysisResults.matched_skills.length}
                    </span>
                  </div>
                  <ul className="space-y-2">
                    {analysisResults.matched_skills.map((skill, index) => (
                      <li key={index} className="flex justify-between items-center py-2 border-b border-gray-100">
                        <span className="text-gray-700">{skill}</span>
                        <span className="w-6 h-6 bg-green-500 text-white text-xs font-bold rounded-full flex items-center justify-center">‚úì</span>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Partial Match Skills */}
                <div className="bg-white border border-gray-200 rounded-lg p-6 shadow-sm">
                  <div className="flex justify-between items-center pb-3 border-b border-gray-200 mb-4">
                    <h3 className="text-lg font-semibold text-gray-800 flex items-center gap-2">
                      <span className="w-3 h-3 bg-yellow-500 rounded-full"></span>
                      Partial Match Skills
                    </h3>
                    <span className="bg-yellow-100 text-yellow-800 text-sm font-semibold px-2 py-1 rounded-full">
                      {analysisResults.partial_skills.length}
                    </span>
                  </div>
                  <ul className="space-y-2">
                    {analysisResults.partial_skills.map((skill, index) => (
                      <li key={index} className="flex justify-between items-center py-2 border-b border-gray-100">
                        <span className="text-gray-700">{skill}</span>
                        <span className="w-6 h-6 bg-yellow-500 text-white text-xs font-bold rounded-full flex items-center justify-center">~</span>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Missing Skills */}
                <div className="bg-white border border-gray-200 rounded-lg p-6 shadow-sm">
                  <div className="flex justify-between items-center pb-3 border-b border-gray-200 mb-4">
                    <h3 className="text-lg font-semibold text-gray-800 flex items-center gap-2">
                      <span className="w-3 h-3 bg-red-500 rounded-full"></span>
                      Missing Skills
                    </h3>
                    <span className="bg-red-100 text-red-800 text-sm font-semibold px-2 py-1 rounded-full">
                      {analysisResults.missing_skills.length}
                    </span>
                  </div>
                  <ul className="space-y-2">
                    {analysisResults.missing_skills.map((skill, index) => (
                      <li key={index} className="flex justify-between items-center py-2 border-b border-gray-100">
                        <span className="text-gray-700">{skill}</span>
                        <span className="w-6 h-6 bg-red-500 text-white text-xs font-bold rounded-full flex items-center justify-center">√ó</span>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>

              {/* Recommendations */}
              <div className="bg-gradient-to-r from-indigo-50 to-purple-50 border-l-4 border-indigo-500 rounded-lg p-6">
                <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
                  <span>üí°</span> Upskilling Recommendations
                </h3>
                <div className="space-y-4">
                  {analysisResults.recommendations.map((rec, index) => (
                    <div key={index} className="flex gap-4 p-4 bg-white rounded-lg shadow-sm border border-gray-200">
                      <div className="flex-shrink-0 w-8 h-8 bg-indigo-500 text-white font-bold rounded-full flex items-center justify-center">
                        {index + 1}
                      </div>
                      <div className="text-gray-700">{rec}</div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-gray-100 border-t border-gray-200 py-6 mt-auto">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex flex-col md:flex-row justify-between items-center gap-4">
            <p className="text-gray-600">AI Role-Based Skill Gap Analyzer &copy; 2025</p>
            <div className="flex items-center gap-4">
              <button 
                onClick={() => alert('Help documentation coming soon!')}
                className="text-blue-600 hover:text-blue-800 transition-colors"
              >
                Help
              </button>
              <span className="text-gray-400">|</span>
              <button 
